mapper.insert:
MERGE INTO SHOP_CART
USING DUAL
ON (MEM_ID = #{memId} AND ITEM_CODE = #{itemCode} AND IS_BUY = 'N')
WHEN MATCHED THEN
	UPDATE
	SET ITEM_CNT = ITEM_CNT + #{itemCnt}
WHEN NOT MATCHED THEN
	INSERT (
		CART_NUM
		, MEM_ID
		, ITEM_CODE
		, ITEM_CNT
	) VALUES (
		SEQ_CART_NUM.NEXTVAL
		, #{memId}
		, #{itemCode}
		, #{itemCnt}
	)

mapper.update:
UPDATE SHOP_CART
SET ITEM_CNT = #{itemCnt}
WHERE CART_NUM = #{cartNum}

mapper.delete:
		DELETE FROM SHOP_CART
		WHERE CART_NUM IN

           	${cartNum}

mapper.insert:
INSERT INTO SHOP_BUY (
	BUY_NUM
	, MEM_ID
	, ITEM_CODE
	, ITEM_CNT
	, BUY_PRICE
	, ORDER_NUM
)

SELECT (SELECT NVL(MAX(BUY_NUM), 0) FROM SHOP_BUY) + #{i} + 1
	, #{item.memId}
	, #{item.itemCode}
	, #{item.itemCnt}
	, #{item.buyPrice}
	, #{item.orderNum}
FROM DUAL

INSERT INTO SHOP_BUY (
	BUY_NUM
	, MEM_ID
	, ITEM_CODE
	, ITEM_CNT
	, BUY_PRICE
	, ORDER_NUM
) VALUES (
	(SELECT NVL(MAX(BUY_NUM), 0) FROM SHOP_BUY) + 1
	, #{memId}
	, #{itemCode}
	, #{itemCnt}
	, #{buyPrice}
	, #{orderNum}
)
