mapper.select:
SELECT MEM_ID
FROM SHOP_MEMBER
WHERE MEM_ID = #{memId}

mapper.insert:
INSERT INTO SHOP_MEMBER (
	MEM_ID
	, MEM_PW
	, MEM_NAME
	, MEM_PHONE
	, MEM_EMAIL
	, MEM_ADDRESS
) VALUES (
	#{memId}
	, #{memPw}
	, #{memName}
	, #{memPhone}
	, #{memEmail}
	, #{memAddress}
)

mapper.update:
UPDATE SHOP_MEMBER
SET MEM_PW = #{memPw}
	, MEM_NAME = #{memName}
	, MEM_PHONE = #{memPhone}
	, MEM_Email = #{memEmail}
	, MEM_ADDRESS = #{memAddress}
WHERE MEM_ID = #{memId}
